package org.example.cifmongo.service;

import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.example.cifmongo.models.Base;
import org.example.cifmongo.models.FirstLevel;
import org.example.cifmongo.models.SecondLevel;
import org.example.cifmongo.models.ThirdLevel;
import org.example.cifmongo.repository.BaseRepository;
import org.springframework.stereotype.Service;
import org.springframework.web.bind.annotation.RequestBody;

import java.util.ArrayList;
import java.util.List;

@Service
@RequiredArgsConstructor
@Slf4j
public class BaseService {

    private final BaseRepository repository;

    private final int repeatable = 50000;
    private final String name = "";
    private final String anything = "";


    public String save() {
        List<Base> list = prepareDate();
        for (var obj : list) {
            repository.save(obj);
        }
        return "Saved successful";

    }

    public Base getById(String id) {
        return repository.findById(id).orElse(null);
    }

    public Base updateById(Base base) {
        return repository.save(base);
    }

    public String updateAllByThirdLevel(ThirdLevel thirdLevel) {
        List<Base> list = repository.findAll();
        for (Base obj : list) {
            FirstLevel firstLevel = obj.getFirstLevel();
            SecondLevel secondLevel = firstLevel.getSecondLevel();
            secondLevel.setThirdLevel(thirdLevel);
            firstLevel.setSecondLevel(secondLevel);
            obj.setFirstLevel(firstLevel);
            repository.save(obj);
        }
        return "Update successful";
    }

    public String updateAllBySecondLevel(SecondLevel secondLevel) {
        var time = System.currentTimeMillis();
        List<Base> list = repository.findAll();
        var time2 = System.currentTimeMillis();
        log.info("======time is {} ms", time2 - time);
        for (Base obj : list) {
            FirstLevel firstLevel = obj.getFirstLevel();
            firstLevel.setSecondLevel(secondLevel);
            obj.setFirstLevel(firstLevel);
            repository.save(obj);
        }
        return "Update successful";
    }

    private List<Base> prepareDate() {
        List<Base> result = new ArrayList<>();
        for (int i = 0; i <= repeatable; i++) {
            ThirdLevel thirdLevel = ThirdLevel.builder()
                    .name(name + i)
                    .price(10 + i)
                    .anything(anything + i)
                    .build();
            SecondLevel secondLevel = SecondLevel.builder()
                    .thirdLevel(thirdLevel)
                    .levelName(anything + i)
                    .levelPrice(15 + i)
                    .build();
            FirstLevel firstLevel = FirstLevel.builder()
                    .firstName("first" + name + i)
                    .secondLevel(secondLevel)
                    .build();
            result.add(new Base(anything + i, firstLevel));
        }
        return result;
    }
}
